getPSDEFormItemUpdates.scriptCode:
console.log("次数更新"+data)

data.attendance_ratio = 1.0;
// data.name = "班次";
var _workNumber = data.working_number ? data.working_number : 1;
var _scopes = [];

// 时间字符串转分钟数的工具函数
function timeToMinutes(timeStr) {
    const parts = timeStr.split(':');
    const hours = parseInt(parts[0], 10);
    const minutes = parseInt(parts[1], 10);
    return hours * 60 + minutes;
}

// 根据班次数量定义时间段配置
var timeConfigs = {
    1: [{ start: "08:00", end: "17:00" }],
    2: [
        { start: "08:00", end: "12:00" },
        { start: "13:30", end: "17:30" }
    ],
    3: [
        { start: "08:00", end: "12:00" },
        { start: "13:30", end: "17:30" },
        { start: "18:30", end: "21:00" }
    ]
};




// 获取当前班次数量的时间配置
var currentConfig = timeConfigs[_workNumber] || timeConfigs[1];

// 生成班次配置并计算总工时
var totalWorkingMinutes = 0;
currentConfig.forEach((timeRange, index) => {
    // 转换时间为分钟数
    const start = timeToMinutes(timeRange.start);
    const end = timeToMinutes(timeRange.end);

    // 计算单个班次工时（分钟）
    totalWorkingMinutes += (end - start);

    //最早提前
    var earliest_checkin = 120
    //最晚延后
    var latest_checkout = 480
    if (_workNumber === 2 && index === 0) {
        earliest_checkin = 120
        latest_checkout = 30
    }
    if (_workNumber === 2 && index === 1) {
        earliest_checkin = 30
        latest_checkout = 480
    }

    if (_workNumber === 3 && index === 0) {
        earliest_checkin = 120
        latest_checkout = 30
    }

    if (_workNumber === 3 && index === 1) {
        earliest_checkin = 30
        latest_checkout = 29
    }

    if (_workNumber === 3 && index === 2) {
        earliest_checkin = 30
        latest_checkout = 180
    }


    // 生成scope对象
    var scope = {
        early_for_absenteeism: 60,
        early_for_early: 0,
        latest_checkout: latest_checkout,
        end_base_time: timeRange.end,
        start_base_time: timeRange.start,
        earliest_checkin: earliest_checkin,
        late_for_absenteeism: 60,
        late_for_late: 0,
        shift_id: data.id,
        order_value: index+1
    };
    _scopes.push(scope);
});

// 转换为小时分钟格式
var hours = Math.floor(totalWorkingMinutes / 60);
var minutes = totalWorkingMinutes % 60;
var workLoadText = hours + "小时" + minutes + "分钟";

// 更新数据
ctrl.setDataValue('scopes', _scopes);
ctrl.setDataValue('work_load', workLoadText);

console.log("初始化总工时" + data)

function timeToMinutes(timeStr) {
    const parts = timeStr.split(':');
    const hours = parseInt(parts[0], 10);
    const minutes = parseInt(parts[1], 10);
    return hours * 60 + minutes;
}
// 初始化总工时
let totalWorkingMinutes = 0;
var _workNumber = data.working_number ? data.working_number : 1;
var start_base_time1 = 0
var end_base_time1 = 0
var start_base_time2 = 0
var end_base_time2 = 0
var start_base_time3 = 0
var end_base_time3 = 0

// 遍历所有班次配置计算总工时
if (data.scopes && data.scopes.length > 0) {
    data.scopes.forEach(scope => {
        // 转换时间段为分钟数
        const start = timeToMinutes(scope.start_base_time);
        const end = timeToMinutes(scope.end_base_time);
        // 累加工时
        if (start >= end) {
            totalWorkingMinutes += (24 * 60 - start + end);
        }else {
            totalWorkingMinutes += (end - start);
        }

        if (scope.order_value === 1) {
            start_base_time1 = start
            end_base_time1 = end
        }
        if (scope.order_value === 2) {
            start_base_time2 = start
            end_base_time2 = end
        }
        if (scope.order_value === 3) {
            start_base_time3 = start
            end_base_time3 = end
        }

    });
}

// 转换为小时分钟格式
const hours = Math.floor(Math.abs(totalWorkingMinutes) / 60);
const minutes = totalWorkingMinutes % 60;

// 更新数据
ctrl.setDataValue('work_load', `${hours}小时${minutes}分钟`);

//计算次日标记
if (data.scopes && data.scopes.length > 0) {
    data.scopes.forEach(scope => {
        if (start_base_time1 >= end_base_time1) {
            if (scope.order_value === 1) {
                scope.next_tag = "0,1"
            } else {
                scope.next_tag = "1,1"
            }
        }
        //两次
        if(_workNumber > 1){
            if (start_base_time2 <= end_base_time1) {
                if (scope.order_value > 1) {
                    scope.next_tag = "1,1"
                }
            }
            if (end_base_time2 <= start_base_time2) {
                if (scope.order_value === 2) {
                    scope.next_tag = "0,1"
                }else if (scope.order_value === 3){
                    scope.next_tag = "1,1"
                }
            }
        }

        //三次
        if(_workNumber === 3){
            if (start_base_time3 <= end_base_time2) {
                if (scope.order_value ===3) {
                    scope.next_tag = "1,1"
                }
            }
            if (end_base_time3 <= start_base_time3) {
                if (scope.order_value === 3){
                    scope.next_tag = "0,1"
                }
            }
        }

    });
    const mdctrl = ctrl.formMDCtrls.find(item => item.name === 'scopes')
    if (mdctrl) {
        mdctrl.loadData()
    }
}
