getPSAppViewLogics.scriptCode:
if (!viewParam.date_range || !viewParam.n_checkin_time_gtandeq || !viewParam.n_checkin_time_ltandeq) {
    const curDate = new Date();
    // 几种默认值处理方式
    const year = curDate.getFullYear();
    const month = curDate.getMonth() + 1;
    const day = curDate.getDate();
    const tempMonth = month < 10 ? `0${month}` : month
    const tempDay = day < 10 ? `0${day}` : day;
    // 今天
    const end = `${year}-${tempMonth}-${tempDay}`;
    // 默认值为今天
    const starttime = `${end} 00:00:00`;
    const endtime = `${end} 23:59:59`
    Object.assign(view.params, {
        date_range: `${starttime},${endtime}`,
        n_checkin_time_gtandeq: starttime,
        n_checkin_time_ltandeq: endtime
    })
    Object.assign(viewParam, {
        date_range: `${starttime},${endtime}`,
        n_checkin_time_gtandeq: starttime,
        n_checkin_time_ltandeq: endtime
    })
}

getPSUIAction.scriptCode:
const viewC = view
const tempPatams = {...viewC.params};
if (viewC.getController("searchform")) {
    const searchForm = viewC.getController("searchform");
    Object.assign(tempPatams, {...searchForm.data});
}
console.log('导出报表参数：', tempPatams);
delete tempPatams.n_checkin_date_gtandeq;
delete tempPatams.n_checkin_date_ltandeq;
delete tempPatams.n_member_id_in;
const urlParam = new URLSearchParams(tempPatams);
const exportUrl = `/attendance_clock_in_records/exportdata/fetch_record_list/?srfexporttag=DataExport&${decodeURIComponent(urlParam)}`;
const app = ibiz.hub.getApp(context.srfappid);
const { data: file } = await app.net.request(exportUrl, {
    method: 'post',
    data: tempPatams,
    responseType: 'blob',
});
if (file) {
    const title = viewC.model.title;
    const fileName = `${title}表.xlsx`;
    const filetype = 'application/vnd.ms-excel';
    const blob = new Blob([file], { type: filetype });
    const href = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = href;
    a.download = fileName;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(href);
}
