const valueArr = view.layoutPanel.panelItems.base_info_checkboxlist.value?.split(',') || [];
['employee_num', 'dept_name', 'title'].forEach(field => {
  const panel = view.layoutPanel.panelItems.base_info_singledata.panelItems[`base_info_${field}`];
  if (panel) {
    panel.state.visible = valueArr.includes(field);
  }
});

//基本信息
['employee_num', 'dept_name', 'title'].forEach(field => {
  const panel = view.layoutPanel.panelItems.base_info_singledata.panelItems[`base_info_${field}`];
  if (panel) panel.state.visible = false;
});

