if (index !== -1) {
  keepVisibleItems.splice(index, 1);
}
// 隐藏部件
const allItems = view.layoutPanel.panelItems;
Object.keys(allItems).forEach(key => {
  if (keepVisibleItems.includes(key)) {
    allItems[key].state.visible = false;
  }
});

const activectrlname = view.layoutPanel.findPanelItemByName('active_ctrl');
activectrlname.setDataValue(curctrl);

view.state.xdatacontrolname =curctrl;

// 获取当前的路由对象
