// 创建一个新的 query 对象，保留现有查询参数，并添加新的参数
const newQuery = {
  ...currentRoute.query, 
  srfnav: curctrl         
};
const defaultctrlname = view.layoutPanel.findPanelItemByName('default_ctrl');
if(defaultctrlname.value == curctrl){
  delete newQuery.srfnav;
}
// 使用 $router.push 更新 URL 并导航到相同的路径
if(newQuery.srfnav != currentRoute.query.srfnav){
  ibiz.appUtil.router.push({
    path: currentRoute.path, 
    query: newQuery 
  });
}

