getPSControlLogics.scriptCode:
const _simpleList = view.getController('simplelist')

const formData = _simpleList.getAllData();
console.log("执行选中校验"+view)

if (formData && formData.length > 0) {
    // 本地分组校验,不通过时直接提示
    const serviceUtil = ibiz.hub.getApp(context.srfappid).deService;
    const groupService = await serviceUtil.getService(context, 'attendanceapp.attendance_group_shift');
    const memberService = await serviceUtil.getService(context, 'attendanceapp.attendance_group_shift_member');
    // 分组数据
    const groups = groupService.local.getList(context);
    // 人员数据
    const items = memberService.local.getList(context);
    const types = ['class_inversion', 'work_rest']
    const selects = formData.map(_data => _data.id);
    // const tags = new Map();
    const groupIndex = [];
    selects.forEach(select => {
        const target = items.find(item => item.group_id !== context.attendance_group_shift && item.user_id === select);
        if (target) {
            // 当前人是被重复选择的,计算当前人之前的分组
            const index = groups.findIndex(group => group.id === target.group_id)
            const tempIndex = index + 1;
            if (!groupIndex.includes(tempIndex)) {
                groupIndex.push(tempIndex)
            }
        }
    })
    if (groupIndex.length > 0) {
        await ibiz.modal.info({
            title: '提示',
            desc: `与第${groupIndex.join('、')}组考勤人员有重合，请修改考勤人员`,
            okText: '我知道了'
        });
        return;
    }
    const cur_members = formData.map(item => {
        return {
            id: item._deData.id,
            name: item._deData.name,
            type: item._deData.type,
            parent_id: item._deData.parent_id
        }
    })
    const app = ibiz.hub.getApp(context.srfappid);
    const res = await app.net.post('/attendance_rules/judging_member', {
        cur_members: JSON.stringify(cur_members),
        //过滤出其他组考勤人员
        other_members: JSON.stringify(items.filter(item => item.group_id !== context.attendance_group_shift)),
        //过滤出item.group_id !== context.attendance_group_shift的再传递
        groups: JSON.stringify(groups.filter(group => group.id !== context.attendance_group_shift)),
        attendance_rule:formData[0]?.attendance_rule
    })
    console.log('校验结果：', res);
    if (res && res.data&& res.data.msg) {
        if(
            await ibiz.modal.confirm({
                title: '提示',
                desc: res.data.msg,
            })
        ){
            if(res.data.isadd){
                view.closeView({ ok: true, data: formData });
            }else {
                return;
            }
        }

    }
}

