getPSControlLogics.scriptCode:
ibiz.uiDomainManager.get(view.context.srfsessionid).evt.on("stateChange", (state, isTrusted )=> {
    console.log("");
    const data = view.getController("form").getData()[0];
    view.getController("toolbar")?.calcButtonState(data,view.model.appDataEntityId);
});

const topContext = view.context;
const appEntityId = view.model.appDataEntityId;
const entityName = appEntityId.split('.').pop();
const bExist = topContext[entityName];
const drbar = view.getCtrl("DRBAR", "drbar");
if(!bExist){
    drbar.disabled({ mode: 'BLANK' })
    //  drbar.disabled({ mode: 'MASK' })

}

view.ctx.evt.on('onRegister', (name, ctrl) => {
   if (name === 'drbar') {
      const topContext = view.context;
      const appEntityId = view.model.appDataEntityId;
      const entityName = appEntityId.split('.').pop();
      const bExist = topContext[entityName];
      if(!bExist){
         ctrl?.disableCounter()
      }
   }
})

getPSDEFormItemUpdates.scriptCode:
var parentId=data.parent_id;
var type=data.type;
if (parentId && type === 'contact') {
        const app = ibiz.hub.getApp(context.srfappid);
        const deService = await app.deService.getService(
                context, "ehrapp.res_partner" // 实体标识
        );
        //将parentId的值赋值到上下文
        const tempContext = Object.assign({},context,{ "res_partner" : parentId });
        const result = await deService.get(tempContext);
        if (result) {
                data.street=result.data.street;
                data.street2=result.data.street2;
                data.zip=result.data.zip;
                data.city=result.data.city;
                data.state_name=result.data.state_name;
                data.country_name=result.data.country_name;
        }
}
